	@Test
	public void test1PacketNoPayload() {
		var buffer = Unpooled.wrappedBuffer(new byte[] {
			(byte) 0x00, (byte) 0x01, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x01, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00,
			(byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00,
		});

		client.run(1, new ChannelInboundHandlerAdapter() {
			@Override
			public void channelActive(ChannelHandlerContext ctx) {
				ctx.writeAndFlush(buffer);
			}

			@Override
			public void channelRead(ChannelHandlerContext ctx, Object msg) {
				if (msg instanceof GamePacket) {
					ctx.close();
				}
			}
		});
	}

	@Test
	public void test3PacketsNoPayload() {
		var buffer0 = Unpooled.wrappedBuffer(new byte[] {
			(byte) 0x00, (byte) 0x01, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x01, (byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff,
			(byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff, (byte) 0xff,
		});
		var buffer1 = buffer0.copy();
		var buffer2 = buffer0.copy();

		var packets = new ArrayList<GamePacket>();
		client.run(3, new ChannelInboundHandlerAdapter() {
			@Override
			public void channelActive(ChannelHandlerContext ctx) {
				ctx.writeAndFlush(buffer0);
				CompletableFuture.delayedExecutor(1, TimeUnit.SECONDS).execute(() -> ctx.writeAndFlush(buffer1));
				CompletableFuture.delayedExecutor(2, TimeUnit.SECONDS).execute(() -> ctx.writeAndFlush(buffer2));
			}

			@Override
			public void channelRead(ChannelHandlerContext ctx, Object msg) {
				if (msg instanceof GamePacket packet) {
					packets.add(packet);
					if (packets.size() >= 3) {
						ctx.close();
					}
				}
			}
		});
	}

